import Foundation
import Testing
import GitHub_OpenAPI

@Suite struct DecodingTests {
    @Test("Push Event Decode - Happy") func testPushEventDecodeHappy() throws {
        do {
           
            let data = pushEventString.data(using: .utf8)
            guard let data else {
                #expect(data != nil, "Data should not be nil")
                return
            }
            
            let match = Github.tryMatchCase(from: data)
            guard let match else {
                #expect(match != nil, "Should have a match")
                return
            }
            
            let decoded: Components.Schemas.WebhookPush = try match.decode(data: data)
            print(decoded)
            
        } catch {
            throw error
        }
        
    }
}

